import React from "react";
import { BrowserRouter as Router, Route, Link, Redirect } from "react-router-dom";
import NavDropdownExample from './components/NavDropdownExample';
import Gear from './components/Gear';
import ExtraGear from './components/ExtraGear';
import GearHeader from './components/GearHeader';
import Calendar from './components/Calendar';
import CalendarTest from './components/CalendarTest';
import Home from './components/Home';
import './App.css';
import { GlobalProvider } from './components/GlobalContext';
import { Navbar } from "react-bootstrap";
import NavbarStranger from "./components/Navbar";
import RegisterModal from "./components/RegisterModal";
import LoginModal from "./components/LoginModal";
import BookSessionModal from "./components/BookSessionModal";
import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faLock, faArrowRight, faArrowLeft, faAngleLeft,faAngleRight } from '@fortawesome/free-solid-svg-icons';
import Footer from './components/Footer';
import Dashboard from './components/Dashboard';
import NotAuthorized from './components/NotAuthorized';
import MySessions from './components/MySessions';
import MySessionsAdmin from './components/MySessionsAdmin';
import YourSessionModal from './components/YourSessionModal';

library.add(faUser);
library.add(faLock);
library.add(faArrowLeft);
library.add(faArrowRight);
library.add(faAngleLeft,faAngleRight)

class App extends React.Component {

  constructor( props ) {
    super(props);
    let user;
    (((this.props.loggedUser.user === null) || (this.props.loggedUser.user === undefined)) ?  user = {
      username: "",
        firstname: "",
        lastname: "",
        email: "",
        role: {"id":"",
              "name":""
        }
                      } : user = this.props.loggedUser.user);
    this.state = {
      token: this.props.loggedUser.token,
      user: user,
      setToken: ( token ) => this.setState({
        token: token
      }),
      setUser: ( user ) => this.setState({
        user: user
      }),
      showLoginModal: false,
      showRegisterModal: false,
      setShowLoginModal: ( value ) => this.setState({
        showLoginModal: value
      }),
      setShowRegisterModal: ( value ) => this.setState({
        showRegisterModal: value
      }),
      sessionToBook: null,
      setSessionToBook: ( value ) => this.setState({
          sessionToBook: value
      }),
      showBookModal: false,
      setShowBookSessionModal: ( value ) => this.setState({
          showBookModal: value
      }),
      yourSession: null,
      setYourSession: ( value ) => this.setState({
          yourSession: value
      }),
      showYourSessionModal: false,
      setShowYourSessionModal: ( value ) => this.setState({
          showYourSessionModal: value
      })
    };
  }
  renderProtectedMySession = () => {
    if (this.state.user.role.id === 2 ) {
      return (props) => <MySessionsAdmin {...props}></MySessionsAdmin>;
    }else if( this.state.user.role.id === 1) {
      return  (props) => <MySessions {...props}/>;  
    }else {
      return (props) => <Redirect to='/notauthorized' />;
    }
  }

  renderProtectedDashboard = () => {
    if (this.state.user.role.id === 2 ) {
      return (props) => <Dashboard {...props}></Dashboard>;
    }else {
      return (props) => <Redirect to='/notauthorized' />;
    }
  }
    // if (this.state.user.role.id === 2 ) {
    //   return (props) => <MySessionsAdmin {...props}></MySessionsAdmin>
    // }
    // if( this.state.user.role.id === 1){
    //   return  (props) => <MySessions {...props}/>;
    // }
    // return (props) => <Redirect to='/notauthorized' />;
    // }

  // componentDidMount(){
  //   this.setState({
  //     token: localStorage.getItem( 'token' ),
  //   // token: "token",
  //   user: {
  //       "username": localStorage.getItem("username"),
  //       "firstname": localStorage.getItem("firstname"),
  //       "lastname": localStorage.getItem("lastname"),
  //       "email": localStorage.getItem("email"),
  //       "role": {
  //         "id": localStorage.getItem("role"),
  //         "name": localStorage.getItem("role")
  //     }
  //   }})
  // }

  render(){
    return (
      <GlobalProvider value = { this.state }>
        <Router>
          <div id="forBackgroundImg">
            <LoginModal></LoginModal>
            <RegisterModal></RegisterModal>
            <BookSessionModal></BookSessionModal>
            <YourSessionModal></YourSessionModal>
            <NavDropdownExample></NavDropdownExample>
    
            <Route exact path="/" component={Home} />
            <Route path="/about" component={About} />
            <Route path="/calendar" component={Calendar} />
            <Route path="/gear" component={GearHeader} />
            <Route path="/mysessions" render={this.renderProtectedMySession()} />
            <Route path="/dashboard" render={this.renderProtectedDashboard()} />
            <Route path="/crew" component={Topics} />
            <Route path="/notauthorized" component={NotAuthorized} />
            <Footer></Footer>
          </div>
        </Router>
      </GlobalProvider>
    );
  }
}

function About() {
  return <h2>About</h2>;
}

function Topic({ match }) {
  return <h3>Requested Param: {match.params.id}</h3>;
}

function Topics({ match }) {
  return (
    <div>
      <h2>Topics</h2>

      <ul>
        <li>
          <Link to={`${match.url}/components`}>Components</Link>
        </li>
        <li>
          <Link to={`${match.url}/props-v-state`}>Props v. State</Link>
        </li>
      </ul>

      <Route path={`${match.path}/:id`} component={Topic} />
      <Route
        exact
        path={match.path}
        render={() => <h3>Please select a topic.</h3>}
      />
    </div>
  );
}

export default App;
